import socketIOClient from "socket.io-client";
import { useState, useEffect } from "react";
import { Card, Container, Row, Col, Button } from "react-bootstrap";
import ModalHeader from "react-bootstrap/esm/ModalHeader";

const ENDPOINT = "http://127.0.0.1:5000";

const Storage = (props) => {
  const [response, setResponse] = useState("");
  const [storageState, setStorageState] = useState(null);
  const [client1, setClient1] = useState({ a: 0, b: 0, c: 0 });
  const [client2, setClient2] = useState({ a: 0, b: 0, c: 0 });
  const [client3, setClient3] = useState({ a: 0, b: 0, c: 0 });
  useEffect(() => {
    const socket = socketIOClient(ENDPOINT);
    socket.on("connection", () => {});
    socket.on("storage_state", (data) => {
      console.log(data);
      setStorageState(data);
      setClient1(data);
      setClient2(data);
      setClient3(data);
    });
  }, []);

  function handleWrite(e, client, storage) {
    const socket = socketIOClient(ENDPOINT, { client });

    socket.emit("write", { client, storage });
  }
  return (
    <Container
      style={{
        maxWidth: "100vw",
        maxHeight: "100vh",
        height: "100vh",
        width: "100vw",
        backgroundColor: "#515151",
        margin: 0,
        padding: 8,
      }}
    >
      <Row
        style={{
          margin: 4,
          padding: 4,
          width: "100%",
          height: 200,
          backgroundColor: "white",
          borderRadius: 4,
        }}
      >
        {storageState != null
          ? Object.keys(storageState).map((storage, status) => {
              return <Col style={{ margin: 2, padding: 2 }}>{storage}</Col>;
            })
          : null}
      </Row>
      <Row
        style={{
          margin: 4,
          padding: 4,
          width: "100%",
          height: 200,
          backgroundColor: "white",
          borderRadius: 4,
        }}
      >
        <Col
          style={{
            padding: 4,
            margin: 4,
            backgroundColor: "#C117E7",
            color: "white",
            fontWeight: "500",
            fontSize: 14,
            borderRadius: 4,
          }}
        >
          <ModalHeader>Client 1</ModalHeader>
          <Row style={{ padding: 4, margin: 0 }}>
            <Col style={{ padding: 2, margin: 0 }}>a</Col>
            <Col style={{ padding: 2, margin: 0 }}>
              <Button
                variant="outline-light"
                style={{
                  padding: "2px 4px 2px 4px",
                  fontSize: 12,
                  margin: "2px 4px 2px 4px",
                }}
              >
                Write
              </Button>
            </Col>
            <Col style={{ padding: 2, margin: 0 }}>
              <Button
                variant="outline-light"
                style={{
                  padding: "2px 4px 2px 4px",
                  margin: "2px 4px 2px 4px",
                  fontSize: 12,
                }}
              >
                Read
              </Button>
            </Col>
          </Row>
          <Row style={{ padding: 4, margin: 0 }}>
            <Col style={{ padding: 2, margin: 0 }}>b</Col>
            <Col style={{ padding: 2, margin: 0 }}>
              <Button
                variant="outline-light"
                style={{
                  padding: "2px 4px 2px 4px",
                  fontSize: 12,
                  margin: "2px 4px 2px 4px",
                }}
              >
                Write
              </Button>
            </Col>
            <Col style={{ padding: 2, margin: 0 }}>
              <Button
                variant="outline-light"
                style={{
                  padding: "2px 4px 2px 4px",
                  margin: "2px 4px 2px 4px",
                  fontSize: 12,
                }}
              >
                Read
              </Button>
