import { io } from "socket.io-client";
import { useState, useEffect, useRef } from "react";
import { toast } from "react-toastify";
import {
  Card,
  Container,
  Row,
  Col,
  Button,
  InputGroup,
  FormControl,
} from "react-bootstrap";
import ModalHeader from "react-bootstrap/esm/ModalHeader";

const ENDPOINT = "http://127.0.0.1:5000";

const Storage = (props) => {
  const socket = useRef(null);
  const [id, setId] = useState(null);
  const [serverId, setServerId] = useState(null);
  const [storageState, setStorageState] = useState(null);

  useEffect(() => {}, [id]);

  function handleId(e) {
    e.preventDefault();
    if (id !== null) {
      socket.current = io(ENDPOINT, { autoConnect: false });
      socket.current.onAny((event, ...args) => {
        console.log(event, args);
      });
      socket.current.auth = { id };
      socket.current.connect();
      socket.current.on("connection", () => {});
      socket.current.on("storage_state", (data) => {
        setStorageState(data);
      });
      socket.current.on("notification", (not) => {
        not.status === "success"
          ? toast.success(not.msg, {
              position: "bottom-right",
              autoClose: 5000,
              hideProgressBar: false,
              closeOnClick: true,
              pauseOnHover: true,
              draggable: true,
              progress: undefined,
            })
          : toast.error(not.msg, {
              position: "bottom-right",
              autoClose: 5000,
              hideProgressBar: false,
              closeOnClick: true,
              pauseOnHover: true,
              draggable: true,
              progress: undefined,
            });
      });
    }
  }
  function handleWrite(e, id, storage) {
    socket.current.emit("write", { storage });
  }
  function handleRead(e, id, storage) {
    socket.current.emit("read", { storage });
  }
  return (
    <Container
      style={{
        margin: "auto",
        maxWidth: 400,
        padding: 8,
        justifyContent: "center",
        alignContent: "center",
      }}
    >
      <Row
        style={{
          margin: 0,
          padding: 8,
          width: "100%",
          backgroundColor: "white",
          borderRadius: 4,
        }}
      >
        <InputGroup className="mb-3">
          <FormControl
            placeholder="Recipient's username"
            aria-label="Recipient's username"
            aria-describedby="basic-addon2"
            value={id}
            onChange={(e) => {
              setId(e.target.value);
            }}
          />
          <InputGroup.Append>
            <Button
              variant="outline-secondary"
              onClick={(e) => {
                handleId(e);
              }}
            >
              Button
            </Button>
          </InputGroup.Append>
        </InputGroup>
      </Row>

      {storageState != null
        ? Object.keys(storageState).map((storage, status) => {
            return (
              <Row
                style={{
                  margin: 8,
                  color: "white",
                  padding: 4,
                  backgroundColor: "#474747",
                  borderRadius: 4,
                }}
              >{`${storage} : ${storageState[storage]}`}</Row>
            );
          })
        : null}

      <Row
        style={{
          margin: 0,
          padding: 4,
          width: "100%",
          height: 200,
          backgroundColor: "white",
          borderRadius: 4,
        }}
      >
        <Card
          style={{
            padding: 4,
            margin: 4,
            backgroundColor: "#C117E7",
            color: "white",
            fontWeight: "500",
            fontSize: 14,
            borderRadius: 4,
            width: "100%",
          }}
        >
          <Row style={{ padding: 4, margin: 0 }}>
            <Col style={{ padding: 2, margin: 0 }}>a</Col>
            <Col style={{ padding: "2px 8px 2px 8px", margin: 0 }}>
              <Button
                variant="outline-light"
                style={{
                  width: "100%",
                  padding: "2px 4px 2px 4px",
                  fontSize: 12,
                  margin: "2px 4px 2px 4px",
                }}
                onClick={(e) => {
                  handleWrite(e, id, "a");
                }}
              >
                Write
              </Button>
            </Col>
            <Col style={{ padding: "2px 8px 2px 8px", margin: 0 }}>
              <Button
                variant="outline-light"
                style={{
                  width: "100%",
                  padding: "2px 4px 2px 4px",
                  margin: "2px 4px 2px 4px",
                  fontSize: 12,
                }}
                onClick={(e) => {
                  handleRead(e, id, "a");
                }}
              >
                Read
              </Button>
            </Col>
          </Row>
          <Row style={{ padding: 4, margin: 0 }}>
            <Col style={{ padding: 2, margin: 0 }}>b</Col>
            <Col style={{ padding: "2px 8px 2px 8px", margin: 0 }}>
              <Button
                variant="outline-light"
                style={{
                  width: "100%",
                  padding: "2px 4px 2px 4px",
                  fontSize: 12,
                  margin: "2px 4px 2px 4px",
                }}
                onClick={(e) => {
                  handleWrite(e, id, "b");
                }}
              >
                Write
              </Button>
            </Col>
            <Col style={{ padding: "2px 8px 2px 8px", margin: 0 }}>
              <Button
                variant="outline-light"
                style={{
                  width: "100%",
                  padding: "2px 4px 2px 4px",
                  margin: "2px 4px 2px 4px",
                  fontSize: 12,
                }}
                onClick={(e) => {
                  handleRead(e, id, "b");
                }}
              >
                Read
              </Button>
            </Col>
          </Row>
          <Row style={{ padding: 4, margin: 0 }}>
            <Col style={{ padding: 2, margin: 0 }}>c</Col>
            <Col style={{ padding: "2px 8px 2px 8px", margin: 0 }}>
              <Button
                variant="outline-light"
                style={{
                  width: "100%",
                  padding: "2px 4px 2px 4px",
                  fontSize: 12,
                  margin: "2px 4px 2px 4px",
                }}
                onClick={(e) => {
                  handleWrite(e, id, "c");
                }}
              >
                Write
              </Button>
            </Col>
            <Col style={{ padding: "2px 8px 2px 8px", margin: 0 }}>
              <Button
                variant="outline-light"
                style={{
                  width: "100%",
                  padding: "2px 4px 2px 4px",
                  margin: "2px 4px 2px 4px",
                  fontSize: 12,
                }}
                onClick={(e) => {
                  handleRead(e, id, "c");
                }}
              >
                Read
              </Button>
            </Col>
          </Row>
        </Card>
      </Row>
    </Container>
  );
};

export default Storage;
