import { io } from "socket.io-client";
import { useState, useEffect, useRef } from "react";
import { toast } from "react-toastify";
import { Card, Container, Row, Col, Button } from "react-bootstrap";
import ModalHeader from "react-bootstrap/esm/ModalHeader";

const ENDPOINT = "http://127.0.0.1:5000";

const Resource = (props) => {
  const socket = useRef(null);
  const [id, setId] = useState(null);
  const [serverId, setServerId] = useState(null);
  const [resourceState, setResourceState] = useState(null);

  useEffect(() => {
    socket.current = io(ENDPOINT, { autoConnect: false });
    socket.current.onAny((event, ...args) => {
      console.log(event, args);
    });
    // socket.current.auth = { id };
    socket.current.connect();
    // console.log(socket.current);
    socket.current.on("connection", () => {});
    socket.current.on("resource_state", (data) => {
      setResourceState(data);
    });
    socket.current.on("notification", (not) => {
      not.status === "success"
        ? toast.success(not.msg, {
            position: "bottom-right",
            autoClose: 5000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
          })
        : toast.error(not.msg, {
            position: "bottom-right",
            autoClose: 5000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
          });
    });
  }, []);

  function handleId(e) {
    e.preventDefault();
    if (id !== null) {
    }
  }
  function handleWrite(e, resource) {
    socket.current.emit("write", { resource });
  }
  function handleRead(e, resource) {
    socket.current.emit("read", { resource });
  }
  function handleRelease(e, resource) {
    socket.current.emit("release", { resource });
  }
  return (
    <Container
      style={{
        margin: "auto",
        maxWidth: 400,
        padding: 8,
        justifyContent: "center",
        alignContent: "center",
      }}
    >
      {socket?.current ? (
        <Row
          style={{
            margin: 8,
            color: "white",
            padding: "4px 16px 4px 16px",
            backgroundColor: "#474747",
            borderRadius: 4,
            fontSize: 12,
          }}
        >
          {`Client id : ${socket.current.id}`}
        </Row>
      ) : null}

      {resourceState != null
        ? Object.keys(resourceState).map((resource, status) => {
            return (
              <Card
                style={{
                  margin: 8,
                  color: "#5A5A5A",
                  padding: 4,
                  backgroundColor: "#FFFFFF",
                  border: "1px solid #5a5a5a",
                  borderRadius: 4,
                }}
              >
                <Row
                  style={{
                    padding: "4px 8px 4px 8px",
                    margin: 2,
                    color: "white",
                    fontSize: 12,
                    backgroundColor: "#135313",
                    border: "1px solid #135313",
                    borderRadius: 4,
                  }}
                >{`${resource} : Written by ${resourceState[resource].write}`}</Row>
                {resourceState[resource].read.map((val) => {
                  return (
                    <Row
                      style={{
                        padding: "4px 8px 4px 8px",
                        margin: 2,
                        color: "white",
                        fontSize: 12,
                        backgroundColor: "#581e73",
                        border: "1px solid #581E73",
                        borderRadius: 4,
                      }}
                    >{`${resource} : Read by ${val}`}</Row>
                  );
                })}
              </Card>
            );
          })
        : null}

      <Row
        style={{
          margin: 0,
          padding: 4,
          width: "100%",
          height: 200,
          backgroundColor: "white",
          borderRadius: 4,
        }}
      >
        <Card
          style={{
            padding: 4,
            margin: 4,
            backgroundColor: "#2C2C2C",
            color: "white",
            fontWeight: "500",
            fontSize: 14,
            borderRadius: 4,
            width: "100%",
          }}
        >
          {resourceState != null
            ? Object.keys(resourceState).map((resource, status) => {
                return (
                  <Row style={{ padding: 4, margin: 0 }}>
                    <Col style={{ padding: 2, margin: 0 }}>{resource}</Col>
                    <Col style={{ padding: "2px 8px 2px 8px", margin: 0 }}>
                      <Button
                        variant="light"
                        style={{
                          width: "100%",
                          padding: "2px 4px 2px 4px",
                          fontSize: 12,
                          margin: "2px 4px 2px 4px",
                        }}
                        onClick={(e) => {
                          handleWrite(e, resource);
                        }}
                      >
                        Write
                      </Button>
                    </Col>
                    <Col style={{ padding: "2px 8px 2px 8px", margin: 0 }}>
                      <Button
                        variant="light"
                        style={{
                          width: "100%",
                          padding: "2px 4px 2px 4px",
                          margin: "2px 4px 2px 4px",
                          fontSize: 12,
                        }}
                        onClick={(e) => {
                          handleRead(e, resource);
                        }}
                      >
                        Read
                      </Button>
                    </Col>
                    <Col style={{ padding: "2px 8px 2px 8px", margin: 0 }}>
                      <Button
                        variant="outline-warning"
                        style={{
                          width: "100%",
                          padding: "2px 4px 2px 4px",
                          margin: "2px 4px 2px 4px",
                          fontSize: 12,
                        }}
                        onClick={(e) => {
                          handleRelease(e, resource);
                        }}
                      >
                        Release
                      </Button>
                    </Col>
                  </Row>
                );
              })
            : null}
        </Card>
      </Row>
    </Container>
  );
};

export default Resource;
