import { io } from "socket.io-client";
import { useState, useEffect, useRef } from "react";
import { toast } from "react-toastify";
import {
  Card,
  Container,
  Row,
  Col,
  Button,
  InputGroup,
  FormControl,
} from "react-bootstrap";
import ModalHeader from "react-bootstrap/esm/ModalHeader";

const ENDPOINT = "http://127.0.0.1:5000";

const Storage = (props) => {
  const socket = useRef(null);
  const [id, setId] = useState(null);
  const [serverId, setServerId] = useState(null);
  const [storageState, setStorageState] = useState(null);

  useEffect(() => {
    socket.current = io(ENDPOINT, { autoConnect: false });
    socket.current.onAny((event, ...args) => {
      console.log(event, args);
    });
    // socket.current.auth = { id };
    socket.current.connect();
    // console.log(socket.current);
    socket.current.on("connection", () => {});
    socket.current.on("storage_state", (data) => {
      setStorageState(data);
    });
    socket.current.on("notification", (not) => {
      not.status === "success"
        ? toast.success(not.msg, {
            position: "bottom-right",
            autoClose: 5000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
          })
        : toast.error(not.msg, {
            position: "bottom-right",
            autoClose: 5000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
          });
    });
  }, []);

  function handleId(e) {
    e.preventDefault();
    if (id !== null) {
    }
  }
  function handleWrite(e, storage) {
    socket.current.emit("write", { storage });
  }
  function handleRead(e, storage) {
    socket.current.emit("read", { storage });
  }
  function handleRelease(e, storage) {
    socket.current.emit("release", { storage });
  }
  return (
    <Container
      style={{
        margin: "auto",
        maxWidth: 400,
        padding: 8,
        justifyContent: "center",
        alignContent: "center",
      }}
    >
      {socket?.current ? (
        <Row
          style={{
            margin: 8,
            color: "white",
            padding: "4px 16px 4px 16px",
            backgroundColor: "#474747",
            borderRadius: 4,
            fontSize: 12,
          }}
        >
          {`Client id : ${socket.current.id}`}
        </Row>
      ) : null}

      <Row
        style={{
          margin: 8,
          color: "white",
          padding: "4px 16px 4px 16px",
          backgroundColor: "#474747",
          borderRadius: 4,
          fontSize: 12,
        }}
      >
        {storageState != null
          ? Object.keys(storageState).map((storage, status) => {
              return (
                <Col
                  style={{
                    margin: 8,
                    color: "#5A5A5A",
                    padding: "4px 16px 4px 16px",
                    backgroundColor: "#FFFFFF",
                    border: "1px solid #5a5a5a",
                    borderRadius: 4,
                  }}
                >
                  <Row
                    style={{
                      padding: 4,
                      margin: 2,
                      border: "1px solid #F78D29",
                      borderRadius: 4,
                    }}
                  >{`${storage} : Written by ${storageState[storage].write}`}</Row>
                  {storageState[storage].read.map((val) => {
                    return (
                      <Row
                        style={{
                          padding: 4,
                          margin: 2,
                          border: "1px solid #56FFE6",
                          borderRadius: 4,
                        }}
                      >{`Read by ${val}`}</Row>
                    );
                  })}
                </Col>
              );
            })
          : null}
      </Row>

      <Row
        style={{
          margin: 0,
          padding: 4,
          width: "100%",
          height: 200,
          backgroundColor: "white",
          borderRadius: 4,
        }}
      >
        <Card
          style={{
            padding: 4,
            margin: 4,
            backgroundColor: "#2C2C2C",
            color: "white",
            fontWeight: "500",
            fontSize: 14,
            borderRadius: 4,
            width: "100%",
          }}
        >
          {storageState != null
            ? Object.keys(storageState).map((storage, status) => {
                return (
                  <Row style={{ padding: 4, margin: 0 }}>
                    <Col style={{ padding: 2, margin: 0 }}>{storage}</Col>
                    <Col style={{ padding: "2px 8px 2px 8px", margin: 0 }}>
                      <Button
                        variant="light"
                        style={{
                          width: "100%",
                          padding: "2px 4px 2px 4px",
                          fontSize: 12,
                          margin: "2px 4px 2px 4px",
                        }}
                        onClick={(e) => {
                          handleWrite(e, storage);
                        }}
                      >
                        Write
                      </Button>
                    </Col>
                    <Col style={{ padding: "2px 8px 2px 8px", margin: 0 }}>
                      <Button
                        variant="light"
                        style={{
                          width: "100%",
                          padding: "2px 4px 2px 4px",
                          margin: "2px 4px 2px 4px",
                          fontSize: 12,
                        }}
                        onClick={(e) => {
                          handleRead(e, storage);
                        }}
                      >
                        Read
                      </Button>
                    </Col>
                    <Col style={{ padding: "2px 8px 2px 8px", margin: 0 }}>
                      <Button
                        variant="outline-warning"
                        style={{
                          width: "100%",
                          padding: "2px 4px 2px 4px",
                          margin: "2px 4px 2px 4px",
                          fontSize: 12,
                        }}
                        onClick={(e) => {
                          handleRelease(e, storage);
                        }}
                      >
                        Release
                      </Button>
                    </Col>
                  </Row>
                );
              })
            : null}
        </Card>
      </Row>
    </Container>
  );
};

export default Storage;
