import { io } from "socket.io-client";
import { useState, useEffect, useRef } from "react";
import { toast } from "react-toastify";
import {
  Card,
  Container,
  Row,
  Col,
  Button,
  InputGroup,
  FormControl,
} from "react-bootstrap";
import ModalHeader from "react-bootstrap/esm/ModalHeader";

const ENDPOINT = "http://127.0.0.1:5000";

const Storage = (props) => {
  const socket = useRef(null);
  const [id, setId] = useState(null);
  const [serverId, setServerId] = useState(null);
  const [storageState, setStorageState] = useState(null);

  useEffect(() => {
    if (id !== null) {
      socket.current = io(ENDPOINT, { autoConnect: false });
      socket.current.onAny((event, ...args) => {
        console.log(event, args);
      });
      socket.current.auth = { id };
      socket.current.connect();
      socket.current.on("connection", () => {});
      socket.current.on("storage_state", (data) => {
        setStorageState(data);
      });
      socket.current.on("notification", (not) => {
        toast.success(not, {
          position: "bottom-right",
          autoClose: 5000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
        });
      });
    }
  }, [id]);

  function handleId(e) {
    e.preventDefault();
    console.log(id);
    socket.current.emit("id", id);
  }
  function handleWrite(e, id, storage) {
    socket.emit("write", { id, storage });
  }
  return (
    <Container
      style={{
        margin: "auto",
        maxWidth: 400,
        padding: 8,
        justifyContent: "center",
        alignContent: "center",
      }}
    >
      <Row
        style={{
          margin: 0,
          padding: 8,
          width: "100%",
          backgroundColor: "white",
          borderRadius: 4,
        }}
      >
        <InputGroup className="mb-3">
          <FormControl
            placeholder="Recipient's username"
            aria-label="Recipient's username"
            aria-describedby="basic-addon2"
            value={id}
            onChange={(e) => {
              setId(e.target.value);
            }}
          />
          <InputGroup.Append>
            <Button
              variant="outline-secondary"
              onClick={(e) => {
                handleId(e);
              }}
            >
              Button
            </Button>
          </InputGroup.Append>
        </InputGroup>
      </Row>
      <Row
        style={{
          margin: 0,
          padding: 4,
          width: "100%",
          backgroundColor: "white",
          borderRadius: 4,
          textAlign: "center",
        }}
      >
        {storageState != null
          ? Object.keys(storageState).map((storage, status) => {
              return (
                <Col
                  style={{
                    margin: 4,
                    color: "white",
                    padding: 2,
                    backgroundColor: "#C117E7",
                    borderRadius: 4,
                  }}
                >{`${storage} : ${storageState[storage]}`}</Col>
              );
            })
          : null}
      </Row>
      <Row
        style={{
          margin: 0,
          padding: 4,
          width: "100%",
          height: 200,
          backgroundColor: "white",
          borderRadius: 4,
        }}
      >
        <Card
          style={{
            padding: 4,
            margin: 4,
            backgroundColor: "#C117E7",
            color: "white",
            fontWeight: "500",
            fontSize: 14,
            borderRadius: 4,
            width: "100%",
          }}
        >
          <Row style={{ padding: 4, margin: 0 }}>
            <Col style={{ padding: 2, margin: 0 }}>a</Col>
            <Col style={{ padding: 2, margin: 0 }}>
              <Button
                variant="outline-light"
                style={{
                  padding: "2px 4px 2px 4px",
                  fontSize: 12,
                  margin: "2px 4px 2px 4px",
                }}
                onClick={(e) => {
                  handleWrite(e, id, "a");
                }}
              >
                Write
              </Button>
            </Col>
            <Col style={{ padding: 2, margin: 0 }}>
              <Button
                variant="outline-light"
                style={{
                  padding: "2px 4px 2px 4px",
                  margin: "2px 4px 2px 4px",
                  fontSize: 12,
                }}
              >
                Read
              </Button>
            </Col>
            <Col
              style={{
                padding: 2,
                margin: 0,
                alignItems: "center",
                display: "flex",
              }}
            >
              <Card
                style={{
                  borderRadius: 5,
                  backgroundColor: "red",
                  width: 10,
                  height: 10,
                  margin: "auto",
                  alignItems: "center",
                }}
              ></Card>
            </Col>
          </Row>
          <Row style={{ padding: 4, margin: 0 }}>
            <Col style={{ padding: 2, margin: 0 }}>b</Col>
            <Col style={{ padding: 2, margin: 0 }}>
              <Button
                variant="outline-light"
                style={{
                  padding: "2px 4px 2px 4px",
                  fontSize: 12,
                  margin: "2px 4px 2px 4px",
                }}
              >
                Write
              </Button>
            </Col>
            <Col style={{ padding: 2, margin: 0 }}>
              <Button
                variant="outline-light"
                style={{
                  padding: "2px 4px 2px 4px",
                  margin: "2px 4px 2px 4px",
                  fontSize: 12,
                }}
              >
                Read
              </Button>
            </Col>
            <Col
              style={{
                padding: 2,
                margin: 0,
                alignItems: "center",
                display: "flex",
              }}
            >
              <Card
                style={{
                  borderRadius: 5,
                  backgroundColor: "red",
                  width: 10,
                  height: 10,
                  margin: "auto",
                  alignItems: "center",
                }}
              ></Card>
            </Col>
          </Row>
          <Row style={{ padding: 4, margin: 0 }}>
            <Col style={{ padding: 2, margin: 0 }}>c</Col>
            <Col style={{ padding: 2, margin: 0 }}>
              <Button
                variant="outline-light"
                style={{
                  padding: "2px 4px 2px 4px",
                  fontSize: 12,
                  margin: "2px 4px 2px 4px",
                }}
              >
                Write
              </Button>
            </Col>
            <Col style={{ padding: 2, margin: 0 }}>
              <Button
                variant="outline-light"
                style={{
                  padding: "2px 4px 2px 4px",
                  margin: "2px 4px 2px 4px",
                  fontSize: 12,
                }}
              >
                Read
              </Button>
            </Col>
            <Col
              style={{
                padding: 2,
                margin: 0,
                alignItems: "center",
                display: "flex",
              }}
            >
              <Card
                style={{
                  borderRadius: 5,
                  backgroundColor: "red",
                  width: 10,
                  height: 10,
                  margin: "auto",
                  alignItems: "center",
                }}
              ></Card>
            </Col>
          </Row>
        </Card>
      </Row>
    </Container>
  );
};

export default Storage;
